MethodDef.FormalExpression:
# Mapping using ODM element ItemData with ItemOID EX.EXENDAT
# Generalized for all StudyEvents
$EXENDAT = xpath(/StudyEventData/FormData[@FormOID='EX.FO']/ItemGroupData[@ItemGroupOID='EX.IG']/ItemData[@ItemOID='EX.EXENDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID EX.EXENTIM
# Generalized for all StudyEvents
$EXENTIM = xpath(/StudyEventData/FormData[@FormOID='EX.FO']/ItemGroupData[@ItemGroupOID='EX.IG']/ItemData[@ItemOID='EX.EXENTIM']/@Value);
$DAT = latestdate($EXENDAT) ;
$TIM = $EXENTIM[last()];
 if($DAT != '' and $TIM != '') {
$DM.RFXENDTC = createdatetime($DAT,$TIM) ;
}

# Mapping using ODM element ItemData with ItemOID PC.PCSTAT
# Generalized for all StudyEvents
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$CODEDVALUE = xpath(/StudyEventData/FormData[@FormOID='PC.FO']/ItemGroupData[@ItemGroupOID='PC.IG']/ItemData[@ItemOID='PC.PCSTAT']/@Value);
if ($CODEDVALUE == 'X') {
  $NEWCODEDVALUE = 'NOT DONE';
} else {
  $NEWCODEDVALUE = ''; 
}
$PC.PCSTAT = $NEWCODEDVALUE;

# Mapping using ODM element ItemData with ItemOID PC.VISITID
# Generalized for all StudyEvents
$PC.VISITNUM = xpath(/StudyEventData/FormData[@FormOID='PC.FO']/ItemGroupData[@ItemGroupOID='PC.IG']/ItemData[@ItemOID='PC.VISITID']/@Value);

# Mapping using ODM element ItemData with ItemOID DMUNT.SEX_DEC
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$CODEDVALUE = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='DMINT.FO']/ItemGroupData[@ItemGroupOID='DMINT.IG']/ItemData[@ItemOID='DMINT.SEX_DEC']/@Value);
if ($CODEDVALUE == 'Female') {
  $NEWCODEDVALUE = 'F';
} elsif ($CODEDVALUE == 'Male') {
  $NEWCODEDVALUE = 'M';
} else {
  $NEWCODEDVALUE = 'NULL'; 
}
$DM.SEX = $NEWCODEDVALUE;

# Mapping using ODM element ItemData with ItemOID PC.PCTIM
# Generalized for all StudyEvents
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$PCTIM = xpath(/StudyEventData/FormData[@FormOID='PC.FO']/ItemGroupData[@ItemGroupOID='PC.IG']/ItemData[@ItemOID='PC.PCTIM']/@Value);

# Mapping using ODM element ItemData with ItemOID PC.PCDAT
# Generalized for all StudyEvents
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$PCDAT = xpath(/StudyEventData/FormData[@FormOID='PC.FO']/ItemGroupData[@ItemGroupOID='PC.IG']/ItemData[@ItemOID='PC.PCDAT']/@Value);
 if($PCDAT != '' and $PCTIM != '') {
$PCDTC = createdatetime($PCDAT,$PCTIM); 
}  else {
$PCDTC = $PCDAT;
} 
if($PCDTC = '' or $RFSTDTC = '') {
$PC.EPOCH = '';
}  elsif($PCDTC < $RFSTDTC) {
$PC.EPOCH = 'SCREENING';
}   elsif ($PCDTC > $RFENDTC) {
$PC.EPOCH = 'FOLLOW-UP';
} elsif ($RFSTDTC <= $PCDTC and $PCDTC <= $RFENDTC) {
$PC.EPOCH = 'TREATMENT';
}

# Mapping using ODM element ItemData with ItemOID DM.RACE
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$CODEDVALUE = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='DM.FO']/ItemGroupData[@ItemGroupOID='DM.IG']/ItemData[@ItemOID='DM.RACE']/@Value);
# Using DECODED values for the SDTM codelist CL.cRACE
if ($CODEDVALUE == '1') {
  $NEWCODEDVALUE = 'BLACK OR AFRICAN AMERICAN';
} elsif ($CODEDVALUE == '5') {
  $NEWCODEDVALUE = 'WHITE';
} elsif ($CODEDVALUE == 'O') {
  $NEWCODEDVALUE = 'OTHER / MULTIPLE';
} elsif ($CODEDVALUE == '3') {
  $NEWCODEDVALUE = 'ASIAN';
} else {
  $NEWCODEDVALUE = 'NULL'; 
}
$DM.RACE = $NEWCODEDVALUE;

# Mapping using ODM element ItemData with ItemOID DM.ETHNIC
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$CODEDVALUE = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='DM.FO']/ItemGroupData[@ItemGroupOID='DM.IG']/ItemData[@ItemOID='DM.ETHNIC']/@Value);
# Using DECODED values for the SDTM codelist CL.cETHNIC
if ($CODEDVALUE == '2') {
  $NEWCODEDVALUE = 'NOT HISPANIC OR LATINO';
} elsif ($CODEDVALUE == '1') {
  $NEWCODEDVALUE = 'HISPANIC OR LATINO';
} else {
  $NEWCODEDVALUE = 'NULL'; 
}
$DM.ETHNIC = $NEWCODEDVALUE;

if($DM.DMDTC != '' and $DM.RFSTDTC != '') {
$DIFF = datediff($DM.DMDTC, $DM.RFSTDTC);
}
$NUM =  number($DIFF ) ;
 if($NUM < 0) {
$DM.DMDY =  $NUM;
} elsif($NUM >= 0) {
$DM.DMDY = $NUM +1;
}  else {
$DM.DMDY = '';
}

# Mapping using ODM element ItemData with ItemOID PE.PEABDO_DEC - value from attribute ItemOID
# Generalized for all Items within the ItemGroup
$ITEM = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='PE.FO']/ItemGroupData[@ItemGroupOID='PE.IG']/ItemData[@ItemOID='PE.PEABDO_DEC' or @ItemOID='PE.PEABRT_DEC' or @ItemOID='PE.PEABBRU_DEC' or @ItemOID='PE.PEABBRF_DEC' or @ItemOID='PE.PEABNBS_DEC' or @ItemOID='PE.PEABOTH_DEC']/@ItemOID);
 if($ITEM = 'PE.PEABDO_DEC') {
$PE.PETESTCD = 'ABDO';
}  elsif($ITEM = 'PE.PEABRT_DEC') {
$PE.PETESTCD = 'ABRT';
}  elsif($ITEM = 'PE.PEABBRU_DEC') {
$PE.PETESTCD = 'ABBRU';
}  elsif($ITEM = 'PE.PEABBRF_DEC') {
$PE.PETESTCD = 'ABBRF';
}  elsif($ITEM = 'PE.PEABNBS_DEC') {
$PE.PETESTCD = 'ABNBS';
}  elsif($ITEM = 'PE.PEABOTH_DEC') {
