}

# Mapping using ODM element ItemData with ItemOID PE.PEDAT
$PEDAT = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='PE.FO']/ItemGroupData[@ItemGroupOID='PE.IG']/ItemData[@ItemOID='PE.PEDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID PE.PETIM
$PETIM = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='PE.FO']/ItemGroupData[@ItemGroupOID='PE.IG']/ItemData[@ItemOID='PE.PETIM']/@Value);
 if($PEDAT != '' and $PETIM != '') {
$PE.PEDTC = createdatetime($PEDAT,$PETIM); 
}  else {
$PE.PEDTC = $PEDAT;
}

if($PE.PEDTC != '' and $RFSTDTC != '') {
$DIFF = datediff($PE.PEDTC, $RFSTDTC);
}
$NUM =  number($DIFF ) ;
 if($NUM < 0) {
$PE.PEDY =  $NUM;
} elsif($NUM >= 0) {
$PE.PEDY = $NUM +1;
}  else {
$PE.PEDY = '';
}

