# Mapping using ODM element ItemData with ItemOID AE.AEENDAT
$AEENDAT = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='AE.FO']/ItemGroupData[@ItemGroupOID='AE.IG']/ItemData[@ItemOID='AE.AEENDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID AE.AEENTIM
$AEENTIM = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='AE.FO']/ItemGroupData[@ItemGroupOID='AE.IG']/ItemData[@ItemOID='AE.AEENTIM']/@Value);
 if($AEENDAT != '' and $AEENTIM != '') {
$AE.AEENDTC = createdatetime($AEENDAT,$AEENTIM); 
}  else {
$AE.AEENDTC = $AEENDAT;
}

$NUM = datediff($AE.AESTDTC, $RFSTDTC);
 if($NUM < 0) {
$AE.AESTDY =  $NUM;
} elsif($NUM >= 0) {
$AE.AESTDY = $NUM +1;
}  else {
$AE.AESTDY = '';
}

if($AE.AEENDTC != '') {
$DIFF = datediff($AE.AEENDTC, $RFSTDTC);
}
$NUM = number($DIFF);
 if($NUM < 0) {
$AE.AEENDY =  $NUM;
} elsif($NUM >= 0) {
$AE.AEENDY = $NUM +1;
}  else {
$AE.AEENDY = '';
}

