# Mapping using ODM element ItemData with ItemOID PRCECT.PRDAT
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$PRDAT = xpath(/StudyEventData/FormData[@FormOID='PR.FO' or @FormOID='PRCECT.FO']/ItemGroupData/ItemData[@ItemOID='PRCECT.PRDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID PRCECT.STTIME
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$PRTIM = xpath(/StudyEventData/FormData[@FormOID='PR.FO' or @FormOID='PRCECT.FO']/ItemGroupData/ItemData[@ItemOID='PRCECT.STTIME']/@Value);
 if($PRDAT != '' and $PRTIM != '') {
$PRSTDTC = createdatetime($PRDAT,$PRTIM); 
}  else {
$PRSTDTC = $PRDAT;
}
if($PRSTDTC = '' or $RFSTDTC = '') {
$PR.EPOCH = '';
}  elsif($PRSTDTC < $RFSTDTC) {
$PR.EPOCH = 'SCREENING';
}   elsif ($PRSTDTC > $RFENDTC) {
$PR.EPOCH = 'FOLLOW-UP';
} elsif ($RFSTDTC <= $PRSTDTC and $PRSTDTC <= $RFENDTC) {
$PR.EPOCH = 'TREATMENT';
}

# Mapping using ODM element ItemData with ItemOID PRCECT.PRDAT
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$PRDAT = xpath(/StudyEventData/FormData[@FormOID='PR.FO' or @FormOID='PRCECT.FO']/ItemGroupData/ItemData[@ItemOID='PRCECT.PRDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID PRCECT.STTIME
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$PRTIM = xpath(/StudyEventData/FormData[@FormOID='PR.FO' or @FormOID='PRCECT.FO']/ItemGroupData/ItemData[@ItemOID='PRCECT.STTIME']/@Value);
 if($PRDAT != '' and $PRTIM != '') {
$PR.PRSTDTC = createdatetime($PRDAT,$PRTIM); 
}  else {
$PR.PRSTDTC = $PRDAT;
}

if($PR.PRSTDTC != '' and $RFSTDTC != '') {
$DIFF = datediff($PR.PRSTDTC, $RFSTDTC);
}
$NUM =  number($DIFF ) ;
 if($NUM < 0) {
$PR.PRSTDY =  $NUM;
} elsif($NUM >= 0) {
$PR.PRSTDY = $NUM +1;
}  else {
$PR.PRSTDY = '';
}

