# with CodeList OID 'CL.C66742.NY'
$CODEDVALUE = xpath(/StudyEventData/FormData[@FormOID='CM.FO' or @FormOID='CMINS.FO' or @FormOID='CMOPI.FO' or @FormOID='CMPHI.FO' or @FormOID='CMVAS.FO']/ItemGroupData/ItemData[@ItemOID='CM.CMTRT' or @ItemOID='CMINS.CMTRT' or @ItemOID='CMOPI.CMTRT' or @ItemOID='CMPH.CMTRT' or @ItemOID='CMVAS.CMTRT']/@ItemOID);
if ($CODEDVALUE == 'CM.CMTRT') {
  $NEWCODEDVALUE = '';
} elsif ($CODEDVALUE == 'CMINS.CMTRT') {
  $NEWCODEDVALUE = '';
} elsif ($CODEDVALUE == 'CMOPI.CMTRT') {
  $NEWCODEDVALUE = 'Y';
} elsif ($CODEDVALUE == 'CMPH.CMTRT') {
  $NEWCODEDVALUE = '';
} elsif ($CODEDVALUE == 'CMVAS.CMTRT') {
  $NEWCODEDVALUE = 'Y';
} elsif ($CODEDVALUE == '') {
  $NEWCODEDVALUE = '';
} else {
  $NEWCODEDVALUE = ''; 
}
$CM.CMPRESP = $NEWCODEDVALUE;

