# Mapping using ODM element ItemData with ItemOID LB.lonicd
# Generalized for all StudyEvents
$LOINCCD = xpath(/StudyEventData/FormData[@FormOID='LB.FO']/ItemGroupData[@ItemGroupOID='LB.IG']/ItemData[@ItemOID='LB.lonicd']/@Value);
 if($LB.LBTESTCD != '') {
$LBLOINC = $LOINCCD;
} 
$LBSTRESU = rws:siunit4loinc($LBLOINC) ;
 if( is-a-number($LB.LBORNRLO)and $LB.LBORRESU != "" and $LBSTRESU != "" and $LBLOINC != '' ) {
$LBSTRESC = rws:unitconversionLoinc($LB.LBORNRLO, $LB.LBORRESU, $LBSTRESU, $LBLOINC  ) ;
}

 if($LBSTRESC != '') {
$LB.LBSTNRLO = $LBSTRESC;
}  else {
$LB.LBSTNRLO = $LB.LBORNRLO;
}

