# Mapping using ODM element ItemData with ItemOID LB.LBDAT
# Generalized for all StudyEvents
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$LBDAT = xpath(/StudyEventData/FormData[@FormOID='LB.FO']/ItemGroupData[@ItemGroupOID='LB.IG']/ItemData[@ItemOID='LB.LBDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID LB.LBTIM
# Generalized for all StudyEvents
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$LBTIM = xpath(/StudyEventData/FormData[@FormOID='LB.FO']/ItemGroupData[@ItemGroupOID='LB.IG']/ItemData[@ItemOID='LB.LBTIM']/@Value);
$LBTIM1 = simpletimetoISOtime($LBTIM);
if($LBDAT != "" and $LBTIM1 != "") {
$LBDT = createdatetime($LBDAT, $LBTIM1) ;
}   elsif($LBDAT != "" and $LBTIM1 == "") {
$LBDT = $LBDAT;
}
$MYSUBJID = xpath(/StudyEventData/../@SubjectKey);
$ALLVSRECORDS = xpath(//SubjectData[@SubjectKey=$MYSUBJID]/StudyEventData/FormData[@FormOID='LB.FO']/ItemGroupData[@ItemGroupOID='LB.IG']);
# the function returns a list of datetimes separated by the pipe (|) symbol
$DATETIMES = my_createdatetimearray($ALLVSRECORDS,'LB.LBDAT','LB.LBTIM');
$DATETIMES_ARRAY = tokenize($DATETIMES,'\|');
 if($RFSTDTC != '') {
$LASTDATETIME = latestorequaldatetimebefore($DATETIMES_ARRAY,$RFSTDTCISO) ;
} 
 
if($LASTDATETIME = $LBDT) {
$LB.LBBLFL = 'Y';
} else { 
$LB.LBBLFL = '';
}

if(contains($CM.CMSTDTC,'UNK')) {
$CM.CMSTDY = '';
}  elsif ($CM.CMSTDTC != ''  and $RFSTDTC != '') {
$DIFF = datediff($CM.CMSTDTC, $RFSTDTC);
}
$NUM =  number($DIFF ) ;
 if($NUM < 0) {
$CM.CMSTDY =  $NUM;
} elsif($NUM >= 0) {
$CM.CMSTDY = $NUM +1;
}  else {
$CM.CMSTDY = '';
}

