# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$CMONGO = xpath(/StudyEventData/FormData[@FormOID='CM.FO' or @FormOID='CMINS.FO' or @FormOID='CMOPI.FO' or @FormOID='CMPHI.FO' or @FormOID='CMVAS.FO']/ItemGroupData/ItemData[@ItemOID='CM.CMONGO' or @ItemOID='CMINS.CMONGO' or @ItemOID='CMPH.CMONGO']/@Value);

if($CMONGO != '' and $CMONGO != 'N' ) {
$CM.CMENRF = 'ONGOING';
}  elsif($CM.CMENDTC != '' and $CM.CMENDTC < $RFSTDTC) {
$CM.CMENRF = 'BEFORE';
}  elsif($CM.CMENDTC != '' and $CM.CMENDTC > $RFENDTC) {
$CM.CMENRF = 'AFTER';
}  elsif($CM.CMENDTC != '' and $RFSTDTC <= $CM.CMENDTC and $CM.CMENDTC < $RFENDTC) {
$CM.CMENRF = 'DURING';
}   elsif($CM.CMENDTC != '' and $RFSTDTC <= $CM.CMENDTC and $CM.CMENDTC = $RFENDTC) {
$CM.CMENRF = 'COINCIDENT';
} else {
$CM.CMENRF = 'UNKNOWN';
}

