$HOICUDAT = xpath(/StudyEventData/FormData[@FormOID='ADM.FO' or @FormOID='DSOUT2.FO' or @FormOID='HOICU.FO' or @FormOID='HOREAD.FO']/ItemGroupData/ItemData[@ItemOID='HOICU.HOSTDAT']/@Value);
