# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$HOICUTIM = xpath(/StudyEventData/FormData[@FormOID='ADM.FO' or @FormOID='DSOUT2.FO' or @FormOID='HOICU.FO' or @FormOID='HOREAD.FO']/ItemGroupData/ItemData[@ItemOID='HOICU.HOSTTIM']/@Value);
 if($HOICUDAT != '' and $HOICUTIM != '') {
$HOICUDTC = createdatetime($HOICUDAT,$HOICUTIM); 
}  else {
$HOICUDTC = $HOICUDAT;
}
