# Mapping using ODM element ItemData with ItemOID ADM.PAGENAME - value from attribute ItemOID
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$ITEMS = xpath(/StudyEventData/FormData[@FormOID='ADM.FO' or @FormOID='DSOUT2.FO' or @FormOID='HOICU.FO' or @FormOID='HOREAD.FO']/ItemGroupData/ItemData[@ItemOID='ADM.ADMDAT' or @ItemOID='DSOUT2.DSDAT2' or @ItemOID='HOICU.HOSTDAT' or @ItemOID='HOREAD.HOSTDAT']/@ItemOID);
 if($ITEMS = 'ADM.ADMDAT') {
$HODTC = $ADMDAT ;
}  elsif($ITEMS = 'DSOUT2.DSDAT2') {
$HODTC = $DSDTC;
}  elsif($ITEMS = 'HOICU.HOSTDAT') {
$HODTC = $HOICUDTC;
}  elsif($ITEMS = 'HOREAD.HOSTDAT') {
$HODTC = $HOREADDAT ;
}
if($HODTC = '' or $RFSTDTC = '') {
$HO.EPOCH = '';
}  elsif($HODTC < $RFSTDTC) {
$HO.EPOCH = 'SCREENING';
}   elsif ($HODTC > $RFENDTC) {
$HO.EPOCH = 'FOLLOW-UP';
} elsif ($RFSTDTC <= $HODTC and $HODTC <= $RFENDTC) {
$HO.EPOCH = 'TREATMENT';
}

