# Mapping using ODM element ItemData with ItemOID DSIC.DSSTTIM
$DSSTTIM = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='DSIC.FO']/ItemGroupData[@ItemGroupOID='DSIC.IG']/ItemData[@ItemOID='DSIC.DSSTTIM']/@Value);
#$DSDTTIM1 = simpletimetoISOtime($DSSTTIM);
 if($DSSTDAT != '' and $DSSTTIM != '') {
$DSIC = createdatetime($DSSTDAT,$DSSTTIM); 
}  else {
$DSIC = $DSSTDAT;
}
# Mapping using ODM element ItemData with ItemOID RN.RNUTCDAT
$RNUTCDAT = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='RN.FO']/ItemGroupData[@ItemGroupOID='RN.IG']/ItemData[@ItemOID='RN.RNUTCDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID RN.RNUTCTIM
$RNUTCTIM = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='RN.FO']/ItemGroupData[@ItemGroupOID='RN.IG']/ItemData[@ItemOID='RN.RNUTCTIM']/@Value);
#$RNUTCTIM1 = simpletimetoISOtime($RNUTCTIM);
 if($RNUTCDAT != '' and $RNUTCTIM != '') {
$RNUTC = createdatetime($RNUTCDAT,$RNUTCTIM); 
}  else {
$RNUTC = $RNUTCDAT;
}
# Mapping using ODM element ItemData with ItemOID RN.RNDAT
$RNDAT = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='RN.FO']/ItemGroupData[@ItemGroupOID='RN.IG']/ItemData[@ItemOID='RN.RNDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID RN.RNTIM
$RNTIM = xpath(/StudyEventData[@StudyEventOID='SE.Screening']/FormData[@FormOID='RN.FO']/ItemGroupData[@ItemGroupOID='RN.IG']/ItemData[@ItemOID='RN.RNTIM']/@Value);
 if($RNDAT != '' and $RNTIM != '') {
$RNDTC = createdatetime($RNDAT,$RNTIM); 
}  else {
$RNDTC = $RNDAT;
}


# Mapping using ODM element ItemData with ItemOID DSOUT.DSDAT
$DSDAT = xpath(/StudyEventData[@StudyEventOID='SE.Study Milestones']/FormData[@FormOID='DSOUT.FO']/ItemGroupData[@ItemGroupOID='DSOUT.IG']/ItemData[@ItemOID='DSOUT.DSDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID DSOUT.STTIME
$DSTIME = xpath(/StudyEventData[@StudyEventOID='SE.Study Milestones']/FormData[@FormOID='DSOUT.FO']/ItemGroupData[@ItemGroupOID='DSOUT.IG']/ItemData[@ItemOID='DSOUT.STTIME']/@Value);
 if($DSDAT != '' and $DSTIME != '') {
$DSOUT = createdatetime($DSDAT,$DSTIME); 
}  else {
$DSOUT = $DSDAT;
}

# Mapping using ODM element ItemData with ItemOID DSOUT2.DSDAT2
$DSDAT2 = xpath(/StudyEventData[@StudyEventOID='SE.Study Milestones']/FormData[@FormOID='DSOUT2.FO']/ItemGroupData[@ItemGroupOID='DSOUT2.IG']/ItemData[@ItemOID='DSOUT2.DSDAT2']/@Value);
# Mapping using ODM element ItemData with ItemOID DSOUT2.STTIME
$DSTIME2 = xpath(/StudyEventData[@StudyEventOID='SE.Study Milestones']/FormData[@FormOID='DSOUT2.FO']/ItemGroupData[@ItemGroupOID='DSOUT2.IG']/ItemData[@ItemOID='DSOUT2.STTIME']/@Value);
 if($DSDAT2 != '' and $DSTIME2 != '') {
$DSOUT2 = createdatetime($DSDAT2,$DSTIME2); 
}  else {
