}
# Mapping using ODM element ItemData with ItemOID DSIC.DSSTDAT - value from attribute ItemOID
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$ITEM =xpath(/StudyEventData/FormData[@FormOID='CONT.FO' or @FormOID='DSIC.FO' or @FormOID='DSOUT.FO' or @FormOID='DSOUT2.FO' or @FormOID='IE.FO' or @FormOID='RN.FO' or @FormOID='DSET.FO']/ItemGroupData/ItemData[@ItemOID='CONT.CONTOCCUR_DEC' or @ItemOID='DSIC.DSSTDAT' or @ItemOID='DSOUT2.DSDAT2' or @ItemOID='IE.IEYN_DEC' or @ItemOID='RN.RNDAT' or @ItemOID='DSET.DSDECOD_DEC']/@ItemOID);

# Mapping using ODM element ItemData with ItemOID IE.IEYN_DEC
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$IEYN =xpath(/StudyEventData/FormData[@FormOID='CONT.FO' or @FormOID='DSIC.FO' or @FormOID='DSOUT.FO' or @FormOID='DSOUT2.FO' or @FormOID='IE.FO' or @FormOID='RN.FO' or @FormOID='DSET.FO']/ItemGroupData/ItemData[@ItemOID='IE.IEYN_DEC']/@Value);

# Mapping using ODM element ItemData with ItemOID CONT.CONTOCCUR_DEC
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
$CONT = xpath(/StudyEventData/FormData[@FormOID='CONT.FO' or @FormOID='DSIC.FO' or @FormOID='DSOUT.FO' or @FormOID='DSOUT2.FO' or @FormOID='IE.FO' or @FormOID='RN.FO' or @FormOID='DSET.FO']/ItemGroupData/ItemData[@ItemOID='CONT.CONTOCCUR_DEC']/@Value);


if($ITEM ='DSIC.DSSTDAT') {
$DSSTDTC = $DSIC;
} elsif($ITEM ='IE.IEYN_DEC' and $IEYN = 'No') {
$DSSTDTC ='';
} elsif($ITEM ='IE.IEYN_DEC' and $IEYN ='Yes') {
$DSSTDTC = $RNUTC;
} elsif($ITEM ='RN.RNDAT' ) {
$DSSTDTC = $RNDAT;
}  elsif($ITEM ='CONT.CONTOCCUR_DEC' and $CONT='Withdrawal of Consent') {
$DSSTDTC =  ''; 
} elsif($ITEM ='CONT.CONTOCCUR_DEC' and $CONT='Discharged') {
$DSSTDTC =  $DSOUT; 
} elsif($ITEM ='DSOUT2.DSDAT2') {
$DSSTDTC = $DSOUT2;
} elsif($ITEM ='DSET.DSDECOD_DEC' ) {
$DSSTDTC =   ''; 
}
if($DSSTDTC = '' or $RFSTDTC = '') {
$DS.EPOCH = '';
}  elsif($DSSTDTC < $RFSTDTC) {
$DS.EPOCH = 'SCREENING';
}   elsif ($DSSTDTC > $RFENDTC) {
$DS.EPOCH = 'FOLLOW-UP';
} elsif ($RFSTDTC <= $DSSTDTC and $DSSTDTC <= $RFENDTC) {
$DS.EPOCH = 'TREATMENT';
}

# Mapping using ODM element ItemData with ItemOID VS.VSTIM
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$VSTIM = xpath(/StudyEventData/FormData[@FormOID='VS.FO']/ItemGroupData/ItemData[@ItemOID='VS.VSTIM']/@Value);

# Mapping using ODM element ItemData with ItemOID VS.VSDAT
# Generalized for all StudyEvents
# Generalized for all Forms within the StudyEvent
# Generalized for all ItemGroups within the Form
# Generalized for all Items within the ItemGroup
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$VSDT = xpath(/StudyEventData/FormData[@FormOID='VS.FO']/ItemGroupData/ItemData[@ItemOID='VS.VSDAT']/@Value);

$MYSUBJID = xpath(/StudyEventData/../@SubjectKey);
$TESTOID = xpath(/StudyEventData/FormData[@FormOID='VS.FO']/ItemGroupData/ItemData[@ItemOID='VS.SYSBP_VSORRES' or @ItemOID='VS.DIABP_VSORRES' or @ItemOID='VS.HR_VSORRES' or @ItemOID='VS.RESP_VSORRES' or @ItemOID='VS.TEMP_VSORRES' or @ItemOID='VS.INTRA_VSORRES' or @ItemOID='VS.HEIGHTP_VSORRES' or @ItemOID='VS.BMI_VSORRES' or @ItemOID='VS.WEIGHT_VSORRES']/@ItemOID);
$VSTIM1 = simpletimetoISOtime($VSTIM);
if($VSDT != "" and $VSTIM1 != "") {
	$VSDTC = createdatetime($VSDT, $VSTIM1) ;
}   elsif($VSDT != "" and $VSTIM1 == "") {
	$VSDTC = $VSDT;
} 

$ALLVSRECORDS = xpath(//SubjectData[@SubjectKey=$MYSUBJID]/StudyEventData/FormData[@FormOID='VS.FO']/ItemGroupData[@ItemGroupOID='VS.IG']);
$DATETIMES = my_createdatetimearray($ALLVSRECORDS,'VS.VSDAT','VS.VSTIM');
$DATETIMES_ARRAY = tokenize($DATETIMES,'\|');
 if($RFSTDTCISO != '') {
$LASTDATETIME = latestorequaldatetimebefore($DATETIMES_ARRAY,$RFSTDTCISO) ;
} 

if($LASTDATETIME = $VSDTC) {
	$VS.VSBLFL = 'Y';
} else { 
	$VS.VSBLFL = '';
}

# Mapping using ODM element StudyEventData using value from attribute StudyEventOID
# Generalized for all StudyEvents
$VISIT = xpath(/StudyEventData/@StudyEventOID/);
 if($VS.VISITNUM != "") {
$VS.VISIT =  substring($VISIT,4);  
}

