 if($EGDAT != '' and $EGTIM != '') {
$EG.EGDTC = createdatetime($EGDAT,$EGTIM);
}  else {
$EG.EGDTC = $EGDAT;
}

$NUM = datediff($EG.EGDTC, $RFSTDTC);
 if($NUM < 0) {
$EG.EGDY =  $NUM;
} elsif($NUM >= 0) {
$EG.EGDY = $NUM +1;
}  else {
$EG.EGDY = '';
}

