# Mapping using ODM element ItemData with ItemOID AE.AESTDAT
$AESTDAT = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='AE.FO']/ItemGroupData[@ItemGroupOID='AE.IG']/ItemData[@ItemOID='AE.AESTDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID AE.AESTTIM
$AESTTIM = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='AE.FO']/ItemGroupData[@ItemGroupOID='AE.IG']/ItemData[@ItemOID='AE.AESTTIM']/@Value);
 if($AESTDAT != '' and $AESTTIM = '') {
$AESTDTC = createdatetime($AESTDAT,$AESTTIM);
}  else {
$AESTDTC = $AESTDAT;
}
if($AESTDTC = '') {
$AE.EPOCH = '';
}  elsif($AESTDTC < $RFSTDTC) {
$AE.EPOCH = 'SCREENING';
}   elsif ($AESTDTC > $RFENDTC) {
$AE.EPOCH = 'FOLLOW-UP';
} elsif ($RFSTDTC <= $AESTDTC and $AESTDTC <= $RFENDTC) {
$AE.EPOCH = 'TREATMENT';
}

