$AESTDAT = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='AE.FO']/ItemGroupData[@ItemGroupOID='AE.IG']/ItemData[@ItemOID='AE.AESTDAT']/@Value);
