$CODEDVALUE = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='EG.FO']/ItemGroupData[@ItemGroupOID='EG.IG']/ItemData[@ItemOID='EG.QTCF_EGORRES']/@ItemOID);
