# Mapping using ODM element ItemData with ItemOID EG.EGDAT
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$EGDAT = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='EG.FO']/ItemGroupData[@ItemGroupOID='EG.IG']/ItemData[@ItemOID='EG.EGDAT']/@Value);
# Mapping using ODM element ItemData with ItemOID EG.EGTIM
# Using categorization as a CodeList is associated with the SDTM CodeList
# but no CodeList is associated with the ODM data
$EGTIM = xpath(/StudyEventData[@StudyEventOID='SE.Patient Level Forms']/FormData[@FormOID='EG.FO']/ItemGroupData[@ItemGroupOID='EG.IG']/ItemData[@ItemOID='EG.EGTIM']/@Value);
$EGTIM1 = simpletimetoISOtime($EGTIM);
 if($EGDAT != '' and $EGTIM1 != '') {
$EGDTC = createdatetime($EGDAT,$EGTIM1);
}  else {
$EGDTC = $EGDAT;
}
$MYSUBJID = xpath(/StudyEventData/../@SubjectKey);
$ALLVSRECORDS = xpath(//SubjectData[@SubjectKey=$MYSUBJID]/StudyEventData/FormData[@FormOID='EG.FO']/ItemGroupData[@ItemGroupOID='EG.IG']);
# the function returns a list of datetimes separated by the pipe (|) symbol
$DATETIMES = my_createdatetimearray($ALLVSRECORDS,'EG.EGDAT','EG.EGTIM');
$DATETIMES_ARRAY = tokenize($DATETIMES,'\|');
 
$LASTDATETIME = latestorequaldatetimebefore($DATETIMES_ARRAY,$RFSTDTCISO) ;
 
if($LASTDATETIME = $EGDTC) {
$EG.EGBLFL = 'Y';
} else { 
$EG.EGBLFL = '';
}

