PROC FREQ DATA=ADSL;
  where ittfl="Y";
  tables trt01pn * (agegr1 sex race) / cmh;
  run;

proc freq data = adef;
  by crit1 avisitn;
  where ittfl="Y";
  tables trtpn * crit1fl / chisq;

proc glm data = adef;
  by avisitn avisit;
  class trtpn;
  model aval = trtpn base;
  run;

proc phreg data = adtte;
  model aval*cnsr(1, 2, 3) = trtpn;
  run;
